$("#search_form").submit((function(t){t.preventDefault();let e=$("input").val();if(0==e.length)alert("Search text can not be empty!");else{let t,o,s;$("#result").html(""),$.get(`https://www.googleapis.com/books/v1/volumes?q=${e}&maxResults=40`,(function(a){for(i=0;i<a.items.length;i++){let l=a.items[i].volumeInfo;o=$('<h1 class="title-color">'+l.title+"</h1>"),o.appendTo("#result");let n="";if(l.authors)for(let t of l.authors)n+=t+" ",t=$('<h2 class="author-color">'+t+"</h2>"),t.appendTo("#result");else s=$('<h2 class="author-color"> author is unknown </h2>'),s.appendTo("#result");l.imageLinks?(t=$('<img> <p><a href="/users/bookdetail/?title='+l.title+"&authors="+n+"&thumbnail="+l.imageLinks.thumbnail+"&more="+l.infoLink+"&desc="+l.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.attr("src",l.imageLinks.thumbnail)):t=$('<h2 class="color-red"> Image is not found in google book database </h2> <p><a href="/users/bookdetail/?title='+l.title+"&authors="+n+"&thumbnail=no&more="+l.infoLink+"&desc="+l.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.appendTo("#result")}})).fail((function(){console.log("failing in get google api")}))}}));