$("#search_form").submit((function(t){t.preventDefault();let e=$("input").val();if(0==e.length)alert("Search text can not be empty!");else{let t,o,a;$.get(`https://www.googleapis.com/books/v1/volumes?q=${e}&maxResults=40`,(function(n){for(i=0;i<n.items.length;i++){let s=n.items[i].volumeInfo;o=$('<h1 class="title-color">'+s.title+"</h1>"),o.appendTo("#result");let l="";if(s.authors)for(let t of s.authors)l+=t+" ",t=$('<h2 class="author-color">'+t+"</h2>"),t.appendTo("#result");else a=$('<h2 class="author-color"> author is unknown </h2>'),a.appendTo("#result");s.imageLinks?(t=$('<img> <p><a href="/users/bookdetail/?title='+s.title+"&authors="+l+"&thumbnail="+s.imageLinks.thumbnail+"&more="+s.infoLink+"&desc="+s.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.attr("src",s.imageLinks.thumbnail)):t=$('<h2 class="color-red"> Image is not found in google book database </h2> <p><a href="/users/bookdetail/?title='+s.title+"&authors="+l+"&thumbnail=no&more="+s.infoLink+"&desc="+s.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.appendTo("#result")}})).fail((function(){console.log("failing in get google api")}))}}));