$("#search_form").submit((function(t){t.preventDefault();let e=$("input").val();if(0==e.length)alert("Search text can not be empty!");else{let t,o,s;$("#result").html(""),$.get(`https://www.googleapis.com/books/v1/volumes?q=${e}&maxResults=40`,(function(l){if(l)for(i=0;i<l.items.length;i++){let a=l.items[i].volumeInfo;o=$('<h1 class="title-color">'+a.title+"</h1>"),o.appendTo("#result");let n="";if(a.authors)for(let t of a.authors)n+=t+" ",t=$('<h2 class="author-color">'+t+"</h2>"),t.appendTo("#result");else s=$('<h2 class="author-color"> author is unknown </h2>'),s.appendTo("#result");a.imageLinks?(t=$('<img> <p><a href="/users/bookdetail/?title='+a.title+"&authors="+n+"&thumbnail="+a.imageLinks.thumbnail+"&more="+a.infoLink+"&desc="+a.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.attr("src",a.imageLinks.thumbnail)):t=$('<h2 class="color-red"> Image is not found in google book database </h2> <p><a href="/users/bookdetail/?title='+a.title+"&authors="+n+"&thumbnail=no&more="+a.infoLink+"&desc="+a.description+"&search="+e+'"><button id="sign-out-button">Read More</button></a></p>'),t.appendTo("#result")}else $('<h1 class="title-color">Books are not found</h1>')})).fail((function(){console.log("failing in get google api")}))}}));